import axios from "axios";
import urlAddress from "../Configuration/urlAddress";

import { ValidatorAdd } from "../Validator/ValidatorAdd";

const headers = {
  "Content-Type": "application/json",
};

const clientService = {
  add,
  edit,
};

function add(client) {
  axios
    .post(
      urlAddress.addClient,
      {
        name: client.name,
        surname: client.surname,
        email: client.email,
      },
      { headers: headers }
    )
    .then((res) => ValidatorAdd(res.data))
    .catch((er) => console.log(er));
}

function edit(client) {
    console.log(window.location.href.charAt(5));

  axios
    .post(
      urlAddress.editClient,
      {
        name: client.name,
        surname: client.surname,
        email: client.email,
      },
      { headers: headers }
    )
    .then((res) => ValidatorAdd(res.data))
    .catch((er) => console.log(er));
}

export default clientService;
